// Dokument-Typ-Erkennung
function detectDocumentType(filename) {
  if (filename.includes('udfs')) return { type: 'UDFS', icon: 'üìã', description: 'User Detailed Functional Specifications', color: '#007cba' };
  if (filename.includes('uhb')) return { type: 'UHB', icon: 'üìñ', description: 'User Handbook', color: '#28a745' };
  if (filename.includes('release')) return { type: 'Release Notes', icon: 'üîÑ', description: 'Release Documentation', color: '#ffc107' };
  return { type: 'ECB Document', icon: 'üìÑ', description: 'ECB Technical Documentation', color: '#6c757d' };
}

const docType = detectDocumentType(fileName);

// Erweiterte Qualit√§tsbewertung
function assessQuality(text) {
  const wordCount = text.split(/\s+/).length;
  const hasStructure = text.includes('Klassifikation') || text.includes('Bewertung');
  const hasRecommendations = text.includes('Empfehlungen') || text.includes('Empfehlung');
  const hasRisks = text.includes('Risiken') || text.includes('Risiko');
  const hasTechnical = text.includes('T2') || text.includes('ECB') || text.includes('TARGET');

  let score = 0;
  if (wordCount > 500) score += 20;
  if (wordCount > 1000) score += 20;
  if (hasStructure) score += 20;
  if (hasRecommendations) score += 15;
  if (hasRisks) score += 15;
  if (hasTechnical) score += 10;

  let confidence = 'Niedrig';
  let confidenceColor = '#dc3545';
  if (score >= 80) { confidence = 'Sehr hoch'; confidenceColor = '#28a745'; }
  else if (score >= 60) { confidence = 'Hoch'; confidenceColor = '#007cba'; }
  else if (score >= 40) { confidence = 'Mittel'; confidenceColor = '#ffc107'; }

  return { score, confidence, confidenceColor, wordCount, hasStructure, hasRecommendations, hasRisks };
}

