    .replace(/^([A-Z√Ñ√ñ√ú][^:\n]*):$/gm, '<h3 style="color: #007cba; margin: 20px 0 10px 0; padding: 8px 12px; background: linear-gradient(135deg, #e3f2fd, #f0f8ff); border-left: 4px solid #007cba; border-radius: 5px;">üîπ $1</h3>')

    // Bold text mit Highlighting
    .replace(/\*\*(.*?)\*\*/g, '<strong style="color: #007cba; background-color: #f0f8ff; padding: 2px 4px; border-radius: 3px;">$1</strong>')

    // Risiken und Empfehlungen hervorheben
    .replace(/\b(Risiko|Gefahr|Achtung|Warnung|Kritisch)\b/gi, '<span style="background: linear-gradient(135deg, #fff3cd, #ffeaa7); color: #856404; padding: 2px 6px; border-radius: 3px; font-weight: 600;">‚ö†Ô∏è $1</span>')
    .replace(/\b(Empfehlung|L√∂sung|Ma√ünahme|Sofortma√ünahme)\b/gi, '<span style="background: linear-gradient(135deg, #d4edda, #c3e6cb); color: #155724; padding: 2px 6px; border-radius: 3px; font-weight: 600;">‚úÖ $1</span>')
    .replace(/\b(T2|ECB|TARGET|RTGS|CLM)\b/g, '<span style="background-color: #e3f2fd; color: #007cba; padding: 1px 4px; border-radius: 2px; font-weight: 600;">$1</span>')

    // Listen formatieren
    .replace(/^- ([^:\n]+):/gm, '<h4 style="color: #495057; margin: 15px 0 8px 0; padding: 6px 10px; background-color: #f8f9fa; border-left: 3px solid #007cba; border-radius: 3px;">‚ñ™Ô∏è $1:</h4>')
